angular.module('veasyTable.templates', []).run(['$templateCache', function($templateCache) {
  $templateCache.put("veasy-table-modal.html",
    "<div class=modal-header><h3 class=modal-title>{{config.i18n.columnFilter.title}}</h3></div><div class=modal-body><ul style=\"list-style-type: none;padding: 0px\" data-as-sortable=board.dragControlListeners data-ng-model=columns><li style=\"width: 100%\" data-ng-repeat=\"column in columns\" data-as-sortable-item class=veasyt-sortable-item><div class=checkbox data-as-sortable-item-handle><label><input type=checkbox ng-model=column.show> {{column.header}}</label></div></li></ul></div><div class=modal-footer><button class=\"btn btn-primary\" ng-click=ok()>{{config.i18n.columnFilter.okButton}}</button> <button class=\"btn btn-default\" ng-click=cancel()>{{config.i18n.columnFilter.cancelButton}}</button></div>");
  $templateCache.put("veasy-table.html",
    "<div class=veasyt-content><div class=row><div class=\"form-group col-md-4 col-sm-4\"><input ng-show=config.filter.enable name=query class=\"pull-left form-control input-sm\" placeholder={{config.i18n.filter.by}} ng-model=query ng-change=\"changeQuery(condition, query)\"></div><div class=\"form-group col-md-8 col-sm-8\"><div ng-show=\"config.filter.enable && config.filter.conditional\" class=btn-group><label class=\"btn btn-default input-sm\" style=\"font-size: 12px\" ng-class=\"{'active': condition === 'AND'}\" ng-model=condition ng-click=\"changeCondition('AND', query)\" btn-radio=\"'AND'\">{{config.i18n.filter.and}}</label><label class=\"btn btn-default input-sm\" style=\"font-size: 12px\" ng-class=\"{'active': condition === 'OR'}\" ng-model=condition ng-click=\"changeCondition('OR', query)\" btn-radio=\"'OR'\">{{config.i18n.filter.or}}</label></div><div class=veasyt-spin><i ng-show=searching class=\"fa fa-spin fa-refresh\"></i></div><button ng-show=config.columnFilter.enable class=\"btn btn-default pull-right\" style=\"font-size: 12px;margin-left: 5px;height: 30px\" ng-click=openColumnFilter(config.columnFilter.modalSize)><i class=\"fa fa-filter\"></i></button> <input ng-show=config.pagination.enable type=number min=1 max=100 name=itemsPerPage class=\"form-control input-sm pull-right\" style=\"width: 65px;display: inline-block\" placeholder=Items ng-model=config.pagination.itemsPerPage ng-change=\"search(condition, query)\"><div class=pull-right style=\"color: #6B6B6B;margin: 7px 5px 0 0;font-size: small\"><span>{{config.i18n.pagination.itemsByPage}}:</span></div></div></div><div class=row><div class=col-md-12><table id={{config.id}} class=\"table table-striped table-hover table-condensed fix-table no-select\" style=\"margin-bottom: 0 !important;border-top: 1px solid #DDD\" ng-mousemove=\"onMove($event, visibleColumns)\" ng-mouseup=onDragStop($event)><thead ng-if=!isLoading class=no-select><tr><th ng-if=config.checkbox.enable ng-style=\"{ width: config.checkbox.size + 'px' }\"><div class=checkbox style=\"margin-top: 0;margin-bottom: 0\"><label style=\"margin-bottom: -5px\"><input type=checkbox ng-model=cb ng-click=checkAllRows(cb)></label></div></th><th ng-repeat=\"column in visibleColumns\" class=truncate ng-style=\"{ width: column.size + 'px' }\"><div style=\"width: 100%;display: inline-flex\"><div ng-if=\"config.resizable.enable && $index > 0\" ng-mousedown=\"onDragStart($event, $index)\" class=veasyt-grip style=\"height: 30px\"><div class=veasyt-resizer ng-class=addResizeBarClass($index)></div></div><div class=use-ellipsis style=\"float:left;cursor: pointer\" ng-click=\"sort(column.value, paginatedList);\"><i ng-show=config.sort.enable class=fa ng-class=\"{'fa-sort-asc': orderByIcon('asc', column.value), 'fa-sort-desc': orderByIcon('desc', column.value), 'fa-sort': orderByIcon('', column.value)}\"></i> {{column.header}}</div></div></th></tr></thead><tbody ng-show=isLoading><tr><td colspan={{colspan}} class=text-center><i class=\"fa fa-spin fa-refresh\"></i></td></tr></tbody><tbody ng-hide=isLoading><tr ng-class=\"{'clickable-row': config.events.onClickRow, 'default-row': !config.events.onClickRow}\" ng-repeat=\"row in paginatedList[currentPage] | orderBy:predicate:reverse\"><td ng-show=config.checkbox.enable class=truncate style=\"vertical-align: middle\"><div><label><input type=checkbox style=\"margin-top: 7px\" ng-model=cbs[getIndex(row)] ng-init=\"cbs[getIndex(row)] = false\" ng-click=checkRow(row)></label></div></td><td ng-repeat=\"column in visibleColumns | orderBy:predicate:reverse\" ng-click=onClickRow(row) style=\"vertical-align: middle\" class=use-ellipsis ng-class=addCellResizeClass($index)>{{row[column.value]}}</td></tr></tbody><tfoot ng-show=\"!isLoading && config.pagination.enable\"><td colspan={{colspan}}><div class=row><div class=\"col-md-6 col-md-offset-3\" style=\"text-align: center\"><ul class=\"pagination veasyt-footer input-sm\"><li ng-class=prevPageDisabled()><a href ng-click=setPage(0) aria-label=Previous><span aria-hidden=true>&laquo;</span></a></li><li ng-class=prevPageDisabled()><a href ng-click=prevPage() aria-label=Previous><span aria-hidden=true>&lsaquo;</span></a></li><li ng-repeat=\"page in pages(paginatedList.length)\" ng-class=\"{active: page === currentPage}\" ng-click=setPage(page)><a href>{{page + 1}}</a></li><li ng-class=nextPageDisabled()><a href ng-click=nextPage() aria-label=Next><span aria-hidden=true>&rsaquo;</span></a></li><li ng-class=nextPageDisabled()><a href ng-click=\"setPage(paginatedList.length - 1)\" aria-label=Next><span aria-hidden=true>&raquo;</span></a></li></ul></div><div class=\"col-md-3 veasyt-total\"><span>{{config.i18n.pagination.totalItems}}: {{totalOfItems}}</span></div></div></td></tfoot><tfoot ng-hide=\"config.pagination.enable || isLoading\"><td colspan={{colspan}}><div class=row><div class=\"col-md-3 col-md-offset-9 veasyt-total veasyt-footer2\"><span>{{config.i18n.pagination.totalItems}}: {{list.length}}</span></div></div></td></tfoot></table></div></div></div>");
}]);
